  const blogPosts = [
    {
      id: 1,
      title: "First Blog Post",
      description: "The will be the first test post on this site.",
      date: "Sep 19"
    },
    {
      id: 2,
      title: "Learning AI",
      description: "How I'm planning to learn AI",
      date: "Sep 24"
    },
    {
      id: 3,
      title: "Building a personal website from scratch with AI",
      description: "Step-by step approach on using Claude and Replit in creating a personal website without knowing how to code.",
      date: "Sep 28"
